sys_ui_page.client_script:
function allotVisitorSlot(){
	var name = gel('visitorname').value;
	var ga = new GlideAjax('ParkingUtils');
	ga.addParam('sysparm_name','getVisitorParkingSlot');
	ga.addParam('sysparm_user',name);
	ga.getXML(parkingVisitorCallback);
}
function parkingVisitorCallback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	var obj = answer.evalJSON();
	if(obj.slot == ''){
		gel('error').innerHTML = obj.msg; 
		gel('slot').innerHTML = '';
		gel('output').innerHTML = '';
	}else{
		gel('error').innerHTML = ''; 
		gel('slot').innerHTML = obj.slot;
		gel('output').innerHTML = "Name: "+obj.user_name+"<br>Entry Time: "+obj.entry_time;
	}
	console.log(JSON.stringify(obj));

}
function getParkingDetails(){
	var user_id = gel('user_id').value;
	var ga = new GlideAjax('ParkingUtils');
	ga.addParam('sysparm_name','getParkingSlot');
	ga.addParam('sysparm_user',user_id);
	ga.getXML(parkingCallback);
}
function parkingCallback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	var obj = answer.evalJSON();
	if(obj.slot == ''){
		gel('error').innerHTML = obj.msg; 
		gel('slot').innerHTML = '';
		gel('output').innerHTML = '';
	}else{
		gel('error').innerHTML = ''; 
		gel('slot').innerHTML = obj.slot;
		gel('output').innerHTML = "Name: "+obj.user_name+"<br>Entry Time: "+obj.entry_time;
	}
	console.log(JSON.stringify(obj));

}
